CREATE TABLE token_items(id int(11) unsigned AUTO_INCREMENT, PRIMARY KEY(id), name VARCHAR(255) NOT NULL, slug VARCHAR(255) NOT NULL, description LONGTEXT, token VARCHAR(255), min_token BIGINT DEFAULT 1, image VARCHAR(255), active TINYINT(1) DEFAULT 0, created_at datetime, updated_at datetime, INDEX(slug), rank int(11) default 0);
CREATE TABLE token_itemPropertyTypes(id int(11) unsigned AUTO_INCREMENT, PRIMARY KEY(id), name VARCHAR(255) NOT NULL, description LONGTEXT, type VARCHAR(255) default 'text', options LONGTEXT, active TINYINT(1) DEFAULT 0, created_at datetime, updated_at datetime, rank int(11) default 0);
CREATE TABLE token_itemProperties(id INT(11) unsigned AUTO_INCREMENT, PRIMARY KEY(id), itemId INT(11) unsigned NOT NULL, index(itemId), FOREIGN KEY(itemId) REFERENCES token_items(id) ON DELETE CASCADE, propertyId INT(11) unsigned NOT NULL, INDEX(propertyId), FOREIGN KEY(propertyId) REFERENCES token_itemPropertyTypes(id) ON DELETE CASCADE, `value` LONGTEXT, updated_at datetime);
